import people from "./data"
import { useState } from "react"

export default function Content() {
    const [index, setIndex] = useState(1)
    const subIndex = () => {
        setIndex((i) => i - 1)
    }
    const addIndex = () => {
        setIndex((i) => i + 1)
    }
    return (
        <div className="container d-flex justify-content-center">
            <div>
            {people.map((person) => {
                const { id, name, job, text } = person
                return (    
                    <div className="card text-center">
                        <div className="card-body" key={id}>
                            <div className="image-container">
                                <img />
                            </div>
                            <p>{name}</p>
                            <h4 className>{job.toUpperCase()}</h4>
                            <p>{text}</p>
                        </div>
                        <div className="buttons d-flex">
                            <button onClick={subIndex} className="btn btn-danger">Prev</button>
                            <button onClick={addIndex} className="btn btn-danger">Next</button>
                        </div>
                    </div>
                )
            })}
            </div>
        </div>
    )
}
